mapper.select:
select u.*,r.roleName as userRoleName from smbms_user u,smbms_role r
where u.userRole = r.id

	AND u.userRole=#{userRole}


	AND u.userName LIKE CONCAT('%',#{userName},'%')

order by creationDate DESC limit #{from},#{pageSize}

select count(1) as count from smbms_user u,smbms_role r where u.userRole = r.id
AND u.userRole=#{userRole}
AND u.userName LIKE CONCAT('%',#{userName},'%')

select u.*,r.roleName as
userRoleName from smbms_user u,smbms_role r where u.userRole = r.id
and u.id=#{id}

mapper.update:
UPDATE smbms_user
userName=#{userName}, userPassword=#{userPassword}, gender=#{gender}, birthday=#{birthday}, phone=#{phone}, address=#{address}, userRole=#{userRole}, modifyBy=#{modifyBy}, modifyDate=#{modifyDate},
WHERE id=#{id};
